#define x first
#define y second

using namespace std;
typedef pair<double, double> pt;
const double e = 1e-8;

/*
 * Find the closest 2 points to the line
 * that point is in the shape depedning on which orientation you are visiting
 * your points
 */

pt operator-(pt a, pt b) {
    return pt(a.x - b.x, a.y - b.y);
}

bool zero(double x) {
  return fabs(x) <= e;
}

double cross(pt a, pt b) {
    return a.x*b.y - a.y*b.x;
}

double ccw(pt a, pt b, pt c) {
    return cross(b - a, c - a);
}

double sq(double d) {
  return d * d;
}

pt mid(pt A, pt B) {
  return {(A.x + B.x) / 2, (A.y + B.y) / 2};
}

double dist(pt A, pt B) {
  return sqrt(sq(A.x - B.x) + sq(A.y - B.y));
}

int contains(vector<pt> shape, pt point) {

  pair<pt, pt> min_points;
  double min_dist = 3 * 1e4;

  for (int i = 0; i < shape.size(); i++) {

    pt A = shape[i];
    pt B = shape[(i + 1) % shape.size()];

    auto d = dist(mid(A, B), point);
    if (d < min_dist) {
      min_points = {A, B};
      min_dist = d;
    }

  }

  auto orientation = ccw(
      min_points.first,
      min_points.second,
      point
  );

  if (zero(orientation)) return 1;
  else if (orientation > 0) return 2;
  else if (orientation < 0) return 0;
  else return 3;
}

int main() {

  vector<pt> shape;

  int g;
  cin >> g;
  for (int i = 0; i < g; ++i) {
    double x, y;
    cin >> x >> y;
    shape.emplace_back(x, y);
  }

  int q;
  cin >> q;
  for (int i = 0; i < q; ++i) {
    double x, y;
    cin >> x >> y;
    cout << contains(shape, {x, y}) << endl;
  }

  return 0;
}

