    return A.x * B.y - A.y * B.x;
}
bool duobianxingnei(vec& t)
{
    int t1, t2, f, sum, i;
    for (i = 0; i <= n; i++)
        p[i].x -= t.x, p[i].y -= t.y; // 坐标平移
    t1 = p[0].x >= 0 ? (p[0].y >= 0 ? 0 : 3) : (p[0].y >= 0 ? 1 : 2); // 计算象限
    for (sum = 0, i = 1; i <= n; i++) {
        if (!p[i].x && !p[i].y)
            break;
        // 被测点为多边形顶点
        f = p[i].y * p[i - 1].x - p[i].x * p[i - 1].y;

        // 计算叉积
        if (!f && p[i - 1].x * p[i].x <= 0 && p[i - 1].y * p[i].y <= 0)
            break; // 点在边上
        t2 = p[i].x >= 0 ? (p[i].y >= 0 ? 0 : 3) : (p[i].y >= 0 ? 1 : 2); // 计算象限
        if (t2 == (t1 + 1) % 4)
            sum += 1;
        // 情况1
        else if (t2 == (t1 + 3) % 4)
            sum -= 1;
        // 情况2
        else if (t2 == (t1 + 2) % 4)
        // 情况3
        {
            if (f > 0)
                sum += 2;
            else
                sum -= 2;
        }
        t1 = t2;
    }
    bool tf = 0;
    if (i <= n || sum)
        tf = 1;
    for (i = 0; i <= n; i++)
        p[i].x += t.x, p[i].y += t.y; // 恢复坐标
    return tf;
}

bool dian_zai_xianshang(vec a, vec b, vec c)
{
    int x1 = a.x, x2 = b.x, y1 = a.y, y2 = b.y;
    if (x1 > x2)
        swap(x1, x2);
    if (y1 > y2)
        swap(y1, y2);
    if (chaji(a, b, c) == 0 &&x1<=c.x&&c.x<=x2&&y1<=c.y&&c.y<=y2)
        return 1;
    return 0;
}
bool zaibianshang(vec& t)
{
    for (int i = 0; i < n; i++)
        if (dian_zai_xianshang(p[i], p[(i + 1) % n], t))
            return 1;
    return 0;
}
