		point A=x-a.first,B=a.second-a.first;
		double l=Length(O,B);
		return B*IP(A,B)/(l*l)+a.first;
	}	
	point Cross_Point(pp a,pp b){
		string s=Parallel_Orthogonal(a,b);
		if(s[0]=='A') return point{inf,inf};
		if(s[0]=='P') return point{inf,0};
		point p=Projection(a,b.first),q=Projection(a,b.second);
		double pl=Length(b.first,p),ql=Length(b.second,q);
		string S=Counter_Clockwise(a,b.first),T=Counter_Clockwise(a,b.second);
		if(S==T) return Exterior(b,pl,ql);
		else return Inter(b,pl,ql);
	}
	string Containment_polyon_point(VP p,point x){
		point c;
		vector<double> cp(p.size());
		string s,t;
		int ans=0;
		p.push_back(p[0]);
		p.push_back(p[1]);
		cp[0]=CP(point{1,0},p[1]-p[0]);
		for(VP::iterator i=p.begin()+1;i!=p.end()-1;i++){
			pp tmp(*i,*(i+1));
			if(i!=p.end()-2) cp[i-p.begin()]=CP(point{1,0},*(i+1)-*i);
			c=Cross_Point(tmp,pp(x,point{x.x+1,x.y}));
			s=Counter_Clockwise(tmp,x);
			t=Counter_Clockwise(tmp,c);
			if(s[3]=='S') return "On_side";
			double cp1=cp[i-p.begin()],cp2=cp[i-p.begin()-1];
			if(c.x==inf||c.x<x.x||c==*(i+1)||t[3]!='S') continue;
			if(c==*i&&((cp1<=0&&cp2>=0)||(cp1>=0&&cp2<=0))) continue;
			ans++;
		}
		if(ans%2==0) return "not_Containment";
		return "Containment";
	}
	void Point_in(point& p){
		cin>>p.x>>p.y;
	}
	void Point_out(point p){
		cout<<p.x<<' '<<p.y<<endl;
	}
};

