	  if (A[i].y==P.y && A[i].x>P.x) A[i].y--,vis[i]=1;//强制使在射线上的点往下移 
	int sum=0;
	for (int i=1;i<=n;i++)
	  if ((vis[i] || vis[i%n+1]) && CJ(Q-P,A[i]-P)*CJ(Q-P,A[i%n+1]-P)<=0) sum++;//修改过的点只需判断所在的边是否在射线两侧 
