inline bool lessX(const Vector &p1, const Vector &p2) {
	return (p1.x < p2.x) || ((p1.x == p2.x) && (p1.y < p2.y));
}
inline polygon getpg(polygon o) {
	sort(o.begin(), o.end(), lessX);
	polygon t, u;
	t.push_back(o[0]);
	t.push_back(o[1]);
	u.push_back(o[0]);
	u.push_back(o[1]);
	for (int i = 2; i < o.size(); i++) {
		for (int j = t.size(); (t.size() > 1) && (ccw(t[t.size() - 2], t[t.size() - 1], o[i]) == 1); j--)
			t.pop_back();
		t.push_back(o[i]);
	}
	for (int i = 2; i < o.size(); i++) {
		for (int j = u.size(); (u.size() > 1) && (ccw(u[u.size() - 2], u[u.size() - 1], o[i]) == -1); j--) {
			u.pop_back();
		}
		u.push_back(o[i]);
	}
	reverse(t.begin(), t.end());
	for (int i = 1; i < t.size() - 1; i++)
		u.push_back(t[i]);
	return u;
}
inline int cont(polygon pg, Vector p) {
	int x = 0;
	for (int i = 0; i < pg.size(); i++) {
		Vector v1 = pg[i] - p;
		Vector v2 = pg[(i + 1)%pg.size()] - p;
		if ((cross(v1, v2) == 0) && (dot(v1, v2) < 0.0)) return 1;
		if (v1.y > v2.y) swap(v1, v2);
		if ((cross(v1, v2) > 0.0) && v1.y < EPS && v2.y > EPS) x++;
	}
	return (x % 2 == 0) ? 0 : 2;
}
