  int n; cin >> n;
  Polygon g(n);
  rep(i, n){
    int x, y; cin >> x >> y;
    g[i] = Point(x, y);
  }

  int q; cin >> q;
  rep(i, q){
    int x, y; cin >> x >> y;
    Point p = Point(x, y);
    cout << contains(g, p) << endl;
  }

  return 0;

}

