const double EPS = 1e-8;

typedef pair<double, double> pt;
typedef pair<pt ,pt> seg;

#define a first
#define b second
#define x first
#define y second

pt operator-(pt a, pt b) { return pt(a.x - b.x, a.y - b.y); }

bool zero(double x) { return fabs(x) <= EPS; }

double cross(pt a, pt b) { return a.x * b.y - a.y * b.x; }

bool ccw(pt a, pt b, pt c) { return cross(b - a, c - a) >= 0; }

double dist(pt a, pt b) { return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2)); }

bool collinear(seg a, seg b) { // all four points collinear
    return zero(cross(a.b - a.a, b.a - a.a)) &&
           zero(cross(a.b - a.a, b.b - a.a));
}
bool intersect(seg a, seg b) {
    if (collinear(a, b)) {
        double maxDist = 0;
        maxDist = max(maxDist, dist(a.a, a.b));
        maxDist = max(maxDist, dist(a.a, b.a));
        maxDist = max(maxDist, dist(a.a, b.b));
        maxDist = max(maxDist, dist(a.b, b.a));
        maxDist = max(maxDist, dist(a.b, b.b));
        maxDist = max(maxDist, dist(b.a, b.b));
        return maxDist < dist(a.a, a.b) + dist(b.a, b.b) + EPS;
    }
    return ccw(a.a, a.b, b.a) != ccw(a.a, a.b, b.b) &&
           ccw(b.a, b.b, a.a) != ccw(b.a, b.b, a.b);
}

vector<pt> shape;
int n;

int ans(pt query)
{
	pt rand = make_pair(47, 199);
	seg l = make_pair(query, rand);
	int count = 0;
	for (int i = 0; i < n; i++)
	{
		pt a = shape[i];
		pt b;
		if (i == n-1){
			b = shape[0];
		} else {
			b = shape[i+1];
		}
		seg d = make_pair(a, b);
		if (intersect(d, make_pair(query, query))) return 1;
		if (intersect(l, d)) count++;
	}
	return count % 2 ? 2 : 0;

}

int main()
{
