	cin >> n;
	for (int i = 0; i < n; i++)
	{
		cin >> x >> y;
		shape.push_back(make_pair(x, y));
	}
	cin >> q;
	for (int i = 0; i < q; i++){
		cin >> x >> y;
		cout << ans(make_pair(x, y)) << endl;
	}
	return 0;
}

