int contains(vector<Point> g,Point p){
  int n=g.size();
  bool x=false;
  for(int i=0;i<n;i++){
  	Point a,b;
	a.x=g[i].x-p.x;
	a.y=g[i].y-p.y;
	b.x=g[(i+1)%n].x-p.x;
