    if(cross(b,c) > EPS)    return +1;  // a → b で 反時計方向におれて c
    if(cross(b,c) < -EPS)    return -1; // a → b で 時計方向におれて c
    if(dot(b,c) < 0)      return +2;  // c -- a -- bで一直線上
