// 凸包
Polygon andrewScan(Polygon s) {
	Polygon u, l;
	int len = s.size();
	if (len < 3) return s;


	// 以x，y为基准升序排序
	sort(s.begin(), s.end());
	// 将x值最小的两个点添加到u
	u.push_back(s[0]);
	u.push_back(s[1]);

	// 将x值最大的两个点添加到l
	l.push_back(s[len - 1]);
	l.push_back(s[len - 2]);

	// 构建凸包上部
	for (int i = 2; i < len; i++) {
		for (int j = u.size(); j >= 2 && ccw(u[j - 2], u[j - 1], s[i]) >= 0; j--) {
			u.pop_back();
		}
		u.push_back(s[i]);
	}
	// 构建凸包下部
	for (int i = len - 3; i >= 0; i--) {
		for (int j = l.size(); j >= 2 && ccw(l[j - 2], l[j - 1], s[i]) >= 0; j--) {
				l.pop_back();
		}
		l.push_back(s[i]);
	}

	reverse(l.begin(), l.end());
	for (int i = u.size() - 2; i >= 1; i--)
		l.push_back(u[i]);

	return l;
}




