int ccw(Point a,Point b,Point c){
  Point t1=b-a,t2=c-a;
  if(t1.det(t2)> eps) return 1;//counter clockwise
  if(t1.det(t2)< -eps) return -1;//clockwise
  if(t1.dot(t2)< -eps) return 2;//c-a-b online
  if(t1.norm()<t2.norm()) return -2;//a-b-c online
  return 0;//a-c-b online
}
int intersectSS(Point a,Point b,Point c,Point d){
    int ret1=ccw(a,b,c)*ccw(a,b,d),ret2=ccw(c,d,a)*ccw(c,d,b);
    if(ret1==0||ret2==0||abs(ret1)>1||abs(ret2)>1) return 2;
    else return (ccw(a,b,c)*ccw(a,b,d)<=0&&ccw(c,d,a)*ccw(c,d,b)<=0);
}
