struct OwnVector2
{
	double x, y;
	OwnVector2(double x, double y) :x(x), y(y) {}
	OwnVector2() :OwnVector2(0, 0) {}
	OwnVector2 operator+(const OwnVector2& v)const
	{
		return OwnVector2(x + v.x, y + v.y);
	}
	OwnVector2 operator-(const OwnVector2& v)const
	{
		return OwnVector2(x - v.x, y - v.y);
	}
	OwnVector2 operator*(const double v)const
	{
		return OwnVector2(x*v, y*v);
	}
	bool operator==(const OwnVector2& v)const
	{
		return abs(x - v.x) <= 0.0000000000001&&abs(y - v.y) <= 0.00000000000001;
	}
	bool operator!=(const OwnVector2& v)const
	{
		return abs(x - v.x) > 0.0000000000001 || abs(y - v.y) > 0.00000000000001;
	}

	double dot(const OwnVector2& v)const
	{
		return x*v.x + y*v.y;
	}
	double cross(const OwnVector2& v)const
	{
		return x*v.y - y*v.x;
	}
	double length()const
	{
		return sqrt(x*x + y*y);
	}
	double length2()const
	{
		return x*x + y*y;
	}
};

OwnVector2 vertices[100];

int JudgeType(OwnVector2 p, int n)
{
	bool judge = false;
	OwnVector2 p0 = vertices[0] - p;

	if (p0.length2() <= 0.000000000001)
	{
		return 1;
	}

	for (int i = 1; i < n + 1; i++)
	{
		OwnVector2 p1 = vertices[(i) % n] - p;
		if (p1.length2() <= 0.00000000001 || (abs(p0.cross(p1)) <= 0.00000000000001 && p0.dot(p1) <= 0.0))
		{
			return 1;
		}

		if (p0.y > p1.y)
		{
			swap(p0, p1);
		}
		if (p0.y <= 0 && 0 <= p1.y)
		{
			if (p0.cross(p1) < 0)
			{
				judge = !judge;
			}
		}

		p0 = vertices[(i) % n];
	}

	return judge ? 0 : 2;
}

