using namespace std;

const double E = 0.000000001;
const int MAX = 100;

struct Point {
	Point operator - (const Point& pos)const {
		Point p;
		p.x = x - pos.x;
		p.y = y - pos.y;
		return p;
	}
	double x, y;
};

struct Line {
	Point p1, p2;
};

Point sub(Point p1, Point p2) {
	Point p;
	p.x = p1.x - p2.x;
	p.y = p1.y - p2.y;
	return p;
}

double dot(Point v1, Point v2) {
	return v1.x * v2.x + v1.y * v2.y;
}

double cross(Point v1, Point v2) {
	return v1.x * v2.y - v1.y * v2.x;
}

double len(Point v) {
	return sqrt(pow(v.x, 2) + pow(v.y, 2));
}

bool lineCross(Line l1, Line l2) {
	auto func = [](Point v1, Point v2, Point v3)->bool {
		int s1 = cross(v1, v2) > 0 ? 1 : -1;
		int s2 = cross(v1, v3) > 0 ? 1 : -1;
		return s1 != s2;
	};

	if (func(sub(l1.p2, l1.p1), sub(l2.p1, l1.p1), sub(l2.p2, l1.p1)) &&
		func(sub(l2.p2, l2.p1), sub(l1.p1, l2.p1), sub(l1.p2, l2.p1))) {
		return true;
	}
}

bool lineOn(Line l, Point p) {
	double c = cross(sub(l.p2, l.p1), sub(p, l.p1));
	if (c < E && c > -E 
		&& dot(sub(l.p2, l.p1), sub(p, l.p1)) > 0
		&& dot(sub(l.p1, l.p2), sub(p, l.p2)) > 0)return true;
	return false;
}

int inPolygon(Point polygon[], int n, Point p) {

	int cnt = 0;
	Line line;

	line.p1 = p;
	line.p2.y = p.y;
	line.p2.x = 10001;

	for (int i = 0; i < n; i++) {
		Line l;
		if (i == 0) {
			l.p1 = polygon[n - 1];
		}
		else {
			l.p1 = polygon[i - 1];
		}
		l.p2 = polygon[i];

		if (lineOn(l, p)) {
			return 1;
		}
		if (lineCross(l, line))cnt++;
	}

	if (cnt & 1)return 2;
	return 0;
}

int main() {
	int n,q;
	Point polygon[MAX];

	cin >> n;
	for (int i = 0; i < n; i++) {
		Point p;
		cin >> polygon[i].x >> polygon[i].y;
	}

	cin >> q;
	for (int i = 0; i < q; i++) {
		Point p;
		cin >> p.x >> p.y;

		cout << inPolygon(polygon, n, p) << endl;
	}

	return 0;
}

