  double lldist(line l){
    if(intersec(l))return 0.0;
    return min(min(pldist(l.a), pldist(l.b)), min(l.pldist(a), l.pldist(b)));
  }
};
struct polygon{
  vector<vec> p;  //ccw
  double area(){
    double res = 0.0;
    for(int i = 0;i < p.size();++i)res += p[i].cross(p[(i + 1) % p.size()]);
    return res / 2.0;
  }
  bool isconv(){
    for(int i = 0;i < p.size();++i)
    if((p[(i + 1) % p.size()] - p[i]).cross(p[(i + 2) % p.size()] - p[i]) < -eps)return false;
    return true;
  }
  int isin(vec a){
    line l = (line){a, a + (vec){xmax - xmin, 0.0}};
    int cnt = 0, n = p.size();
    for(int i = 0;i < n;++i){
      line tmp = (line){p[i], p[(i + 1) % n]};
      if(tmp.ccw(a) == 0)return 1; //on line
      if(l.intersec(tmp)){
        ++cnt;
        if(l.ccw(p[i]) == 0 && l.ccw(p[(i + 1) % n]) * l.ccw(p[(i + n - 1) % n]) == -1)++cnt;
        if(l.paral(tmp)){
          ++cnt;
          if(l.ccw(p[(i + 2) % n]) * l.ccw(p[(i + n - 1) % n]) == -1)++cnt;
        }
      }
    }
    return (cnt & 1) * 2;  //2:in 0:out
  }
};

