using namespace std;
using ll = long long;
template<typename T = ll>inline T in() { T x; cin >> x; return x; }

class polygon {
private:
	vector<pair<int, int>>vertex;
public:
	static const int on_vertex = 0;
	static const int on_side = 1;
	static const int inside = 2;
	static const int outside = 3;
	polygon(int n) {
		vertex.reserve(n);
	}
	void push_back(int x, int y) {
		vertex.push_back(make_pair(x, y));
	}
	int contains(int x, int y) {
		int ret = 0;
		int num = vertex.size();
		REP(i, num) {
			if (make_pair(x, y) == vertex[i]) {
				return on_vertex;
			} else {
				int x0 = vertex[i].first, y0 = vertex[i].second, x1 = vertex[(i + 1) % num].first, y1 = vertex[(i + 1) % num].second;
				if (y0 != y1) {
					double cross_x = (double)((x0 * y1 - x1 * y0) + (x1 - x0) * y) / (y1 - y0);
					if (min(y0, y1) <= y && y < max(y0, y1)) {
						if (abs(cross_x - x) < EPS) {
							return on_side;
						} else if (cross_x > x) {
							++ret;
						}
					}
				} else {
					if (y0 == y && (x0 < x && x < x1 || x1 < x && x < x0)) {
						return on_side;
					}
				}
			}
		}
		return ret % 2 == 1 ? inside : outside;
	}
};
signed main() {
	int n = in();
	polygon pol(n);
	REP(i, n) {
		int x = in(), y = in();
		pol.push_back(x, y);
	}
	int q = in();
	REP(i, q) {
		int x = in(), y = in();
		switch (pol.contains(x, y)) {
		case polygon::outside:
			cout << 0 << endl;
			break;
		case polygon::on_vertex:
		case polygon::on_side:
			cout << 1 << endl;
			break;
		case polygon::inside:
			cout << 2 << endl;
			break;
		}
	}
}

