		b.out();
		printf("\n");
		return ;
	}
};
struct line{
	double A;
	double B;
	double C;
	void pull(){
		if(ABS(A)<eps){
			A/=B;
			C/=B;
			B/=B;
		}
		else{
			B/=A;
			C/=A;
			A/=A;
		}
		return ;
	}
	line(){}
	line(double a,double b,double c){A=a;B=b;C=c;pull();}
	line(point a,point b){
		A=a.y-b.y;
		B=b.x-a.x;
		C=A*a.x+B*a.y;
		pull();
	}
	line(double a,double b,point p){
		A=a;
		B=b;
		C=A*p.x+B*p.y;
		pull();
	}
	line(segment s){
		A=s.a.y-s.b.y;
		B=s.b.x-s.a.x;
		C=A*s.a.x+B*s.a.y;
		pull();
	}
	void out(){
		printf("line:: A=%.3lf B=%.3lf C=%.3lf\n",A,B,C);
		return ;
	}
};
double distance(point a,point b){return (a-b).length();}
double dot(point a,point b){return a.x*b.x+a.y*b.y;}
double cross(point a,point b){return a.x*b.y-b.x*a.y;}
//angle only returns positive value
double angle(point a,point b){return acos(dot(a,b)/(a.length()*b.length()));}
double area(point a,point b,point c){return ABS(cross(b-a,c-a));}
double distance(point a,line b){return (b.A*a.x+b.B*a.y-b.C)/sqrt(b.A*b.A+b.B*b.B);}
point arbitrary_point(line l){
	if(ABS(l.A)<=eps)return point(0,l.C/l.B);
	else return point(l.C/l.A,0);
}
bool between(point a,point b,point c){
	if((a.x-b.x<=eps&&b.x-c.x<=eps)||(eps>=b.x-a.x&&eps>=c.x-b.x))
	if((a.y-b.y<=eps&&b.y-c.y<=eps)||(eps>=b.y-a.y&&eps>=c.y-b.y))
	return true;
	return false;
}
point intersect(line a,line b){
	double x,y,det;
	x=(a.C*b.B-b.C*a.B);
	y=(a.A*b.C-b.A*a.C);
	det=(a.A*b.B-b.A*a.B);
	return point(x,y)/det;
}
bool parallel(line a,line b){return same(a.A*b.B,b.A*a.B);}
double distance(line a,line b){
	if(parallel(a,b))return distance(arbitrary_point(a),b);
	else return 0;
}
double distance(point p,segment s){
	line l(s);
	point a(intersect(line(l.B,-l.A,l.B*p.x-l.A*p.y),l));
	if(between(s.a,a,s.b))return distance(p,a);
	else return min(distance(p,s.a),distance(p,s.b));
}
double distance(segment a,segment b){
	point l(a.a),lm,rm,r(a.b);
	for(int i=0;i<100;i++){
		lm=(l*2+r)/3;
		rm=(l+r*2)/3;
		if(distance(lm,b)>distance(rm,b))l=lm;
		else r=rm;
	}
	return distance(l,b);
}
bool same(line a,line b){return ABS(distance(a,b))<=eps;}
bool vertical(line a,line b){return ABS(a.A*b.A+a.B*b.B)<=eps;}
bool hasintersect(segment a,segment b){
	line l(a),r(b);
	if(parallel(l,r)){
		if(same(l,r))return between(a.a,b.a,a.b)||between(a.a,b.b,a.b)||between(b.a,a.a,b.b)||between(b.a,a.b,b.b);
		else return false;
	}
	else{
		point p(intersect(l,r));
		return between(a.a,p,a.b)&&between(b.a,p,b.b);
	}
}
bool hasintersect(segment a,line b){
	line l(a);
	if(parallel(l,b))return same(l,b);
	else return between(a.a,intersect(l,b),a.b);
}
point intersect(segment a,segment b){return intersect(line(a),line(b));}
point intersect(segment a,line b){return intersect(line(a),b);}
point projection(point a,line b){return intersect(line(b.B,-b.A,a),b);}
point reflection(point a,line b){return projection(a,b)*2-a;}
bool online(point p,line l){return ABS(l.A*p.x+l.B*p.y-l.C)<eps;}
bool onsegment(point p,segment s){return online(p,line(s))&&between(s.a,p,s.b);}
double area(vector<point> &v){
	double ans=0;
	int n=(int)v.size();
	for(int i=1;i<n;i++)ans+=cross(v[i],v[i-1]);
	ans+=cross(v[0],v[n-1]);
	return ABS(ans)/2;
}
bool isconvex(vector<point> &v){
	bool f=true;
	int n=(int)v.size();
	vector<point> nv(v);
	nv.PB(v[0]);
	nv.PB(v[1]);
	for(int i=0;i<n;i++)if(cross(v[i+1]-v[i],v[i+2]-v[i])<-eps){
		f=false;
		break;
	}
	if(f)return true;
	f=true;
	reverse(nv.begin(),nv.end());
	for(int i=0;i<n;i++)if(cross(v[i+1]-v[i],v[i+2]-v[i])<-eps)return false;
	return true;
}
bool onpolygon(point p,vector<point> &v){
	int n=(int)v.size();
	for(int i=1;i<n;i++)if(onsegment(p,segment(v[i],v[i-1])))return true;
	return onsegment(p,segment(v[0],v[n-1]));
}
bool inpolygon(point p,vector<point> &v){
	bool f=false;
	int n=(int)v.size();
	for(int i=0,j=n-1;i<n;j=i++)if((dcmp(v[i].y-p.y)>0!=dcmp(v[j].y-p.y)>0)&&dcmp(p.x-(p.y-v[i].y)*(v[i].x-v[j].x)/(v[i].y-v[j].y)-v[i].x)<0)f=!f;
	return f;
}
