// 点的内包
/*
	IN 2
	ON 1
	OUT 0
*/
int contains(Polygon g, Point p) {
	int n = g.size();
	bool x = false;
	for (int i = 0; i < n; i++) {
		Point a = g[i] - p, b = g[(i + 1) % n] - p;
		if (abs(cross(a, b)) < EPS && dot(a, b) < EPS) return 1;
		if (a.y > b.y) swap(a, b);
		if (a.y < EPS && EPS < b.y && cross(a, b) > EPS)
			x = !x;
	}
	return (x ? 2 : 0);
}

// 凸包
Polygon andrewScan(Polygon s) {
	Polygon u, l;
	int len = s.size();
	if (len < 3) return s;
	// 以x，y为基准升序排序
	sort(s.begin(), s.end());
	// 将x值最小的两个点添加到u
	u.push_back(s[0]);
	u.push_back(s[1]);

	// 将x值最大的两个点添加到l
	l.push_back(s[len - 1]);
	l.push_back(s[len - 2]);

	// 构建凸包上部
	for (int i = 2; i < len; i++) {
		for (int j = u.size(); j >= 2 && ccw(u[j - 2], u[j - 1], s[i]) != CLOCKWISE; j--)
			u.pop_back();
		u.push_back(s[i]);
	}

	// 构建凸包下部
	for (int i = len - 3; i >= 0; i--) {
		for (int j = l.size(); j >= 2 && ccw(l[j - 2], l[j - 1], s[i]) != CLOCKWISE; j--)
			l.pop_back();
		l.push_back(s[i]);
	}

	// 按顺时针方向生成凸包的点的序列
	reverse(l.begin(), l.end());
	for (int i = u.size() - 2; i >= 1; i--)
		l.push_back(u[i]);

	return l;
}


