struct Point{
    double x;
    double y;
    Point(double x, double y) : x(x), y(y) {}
    Point(){}
    Point operator-(Point p){
        return Point(x - p.x, y - p.y);
    }
};
typedef Point Vector;
double cross(Vector v1, Vector v2){
    return v1.x * v2.y - v1.y * v2.x;
}

struct Segment{
    Point p1;
    Point p2;
    Segment(Point p1, Point p2) : p1(p1), p2(p2) {}
    Segment(){}
};
typedef Segment Line;

bool is_online(Point p, Segment s){
    return abs(cross(s.p1 - p, s.p2 - p)) <= 1e-10;
}

bool is_intersect(Line l, Segment s){
    Vector vl  = l.p2 - l.p1;
    Vector vp1 = s.p1 - l.p1;
    Vector vp2 = s.p2 - l.p1;
    if (cross(vl, vp1) * cross(vl, vp2) <= 0) return true;
    return false;
}

typedef vector<Point> Polygon;
int determine_containment(Point p, Polygon poly){
    int c = 0;
    Line l_right(p, Point(p.x + 1, p.y));
    for (int i = 0, n = poly.size(); i < n; i++) {
        Segment s(poly[i], poly[(i+1)%n]);
        if (is_online(p, s)) return 1;
        c += is_intersect(l_right, s);
    }
    if (c % 2) return 2;
    return 0;
}
