	return sqrt(norm(p));
}

double dot(Vector a,Vector b){
	return add(a.x*b.x,a.y*b.y);
}

double cross(Vector a,Vector b){
	return add(a.x*b.y,-a.y*b.x);
}


int contains(Polygon g,P p){
	int n=g.size();
	bool x=false;
	for(int i=0;i<n;i++){
		P a=g[i]-p,b=g[(i+1)%n]-p;
		if(abs(cross(a,b))<EPS && dot(a,b)<EPS)return 1;
		if(a.y>b.y)swap(a,b);
		if(a.y<EPS && EPS<EPS && cross(a,b)>EPS)x=!x;
	}
	return (x?2:0);
}

