}

Point projection(Point p1, Point p2, Point p3) //直線p1p2 上への写像
{
    double t = dot(p3 - p1, p2 - p1) / norm(p2 - p1);
    return (p1 + (p2 - p1) * t);
}
Point reflection(Point p1, Point p2, Point p3) //直線p1p2 上への反射
{
    return (p3 + (projection(p1, p2, p3) - p3) * 2);
}
double distanceLP(Point p1, Point p2, Point p)
{
    return (abs(p - projection(p1, p2, p)));
}
double distanceLL(Point l1, Point l2, Point m1, Point m2)
{
    return (isIntersectLL(l1, l2, m1, m2) ? 0 : distanceLP(l1, l2, m1));
}
double distanceLS(Point l1, Point l2, Point s1, Point s2)
{
    if (isIntersectLS(l1, l2, s1, s2)) return (0);
    return (min(distanceLP(l1, l2, s1), distanceLP(l1, l2, s2)));
}
double distanceSP(Point s1, Point s2, Point p)
{
    Point r = projection(s1, s2, p);
    if (isIntersectSP(s1, s2, r)) return (abs(r - p));
    return (min(abs(s1 - p), abs(s2 - p)));
}
double distanceSS(Point s1, Point s2, Point t1, Point t2){
    if (isIntersectSS(s1, s2, t1, t2)) return (0);
    return (min(min(distanceSP(s1, s2, t1), distanceSP(s1, s2, t2)),
            min(distanceSP(t1, t2, s1), distanceSP(t1, t2, s2))));
}
Point interPointLL(Point a1, Point a2, Point b1, Point b2)
{
    double d = cross(a2 - a1, b2 - b1);
    if (abs(d) < EPS) throw "Segment is Paralell!";
    Point a = a2 - a1, b = b2 - b1;
    double t = cross(b, b1 - a1) / cross(b, a);
    return (a1 + a * t);
}
vector<Point> interPointCC(Point c1, double r1, Point c2, double r2)
{
    vector<Point> res;
    if (abs(c1 - c2) < EPS) return (vector<Point>()); // 交点が絶対にない
    double d = abs(c1 - c2);
    double rc = (d * d + r1 * r1 - r2 * r2) / (2 * d);
    double rs = sqrt(r1 * r1 - rc * rc);
    Point diff = (c2 - c1) / d;
    res.push_back(Point(c1 + diff * Point(rc, rs)));
    res.push_back(Point(c1 + diff * Point(rc, -rs)));
    return (res);
}
vector<Point> crosspointCL(Point a, double ra, Point b1, Point b2)
{
    vector<Point> res;
    double dist = distanceLP(b1, b2, a);
    if (leq(dist, ra)){
        Point s = projection(b1, b2, a);
        dist = sqrt(ra * ra - dist * dist);
        Point t = (b2 - b1) / abs(b2 - b1);
        res.push_back(s + t * dist);
        res.push_back(s - t * dist);
    }
    return (res);
}

Point unitVector(Vector t)
{
    double u = abs(t);
    return (Point(t.x / u , t.y / u));
}

Point normalVector(Point p, Point a, Point b)
{
    Point v = unitVector(b - a);
    v = cross(v, p - a) > 0 ? Point(v.y, -v.x) : Point(-v.y, v.x);
    return v * distanceLP(p, a, b);
}

double getCos(Vector a, Vector b)
{
    return (dot(a, b) / (abs(a) * abs(b)));
}

double getSin(Vector a, Vector b)
{
    double t = getCos(a, b);
    return (sqrt(1.0 - t * t));
}

double getArg(Vector v)
{
    return (atan2(v.y, v.x));
}

Point rotate(Point t, Point p, double r)
{
    double ta = cos(r) * (t.x - p.x) - sin(r) * (t.y - p.y) + p.x;
    double tb = sin(r) * (t.x - p.x) + cos(r) * (t.y - p.y) + p.y;
    return (Point(ta, tb));
}

