#include <iostream>
#include <cmath>
using namespace std;

struct P {
    int x, y;

    P() : x(0), y(0) { }
    P(int x, int y) : x(x), y(y) { }

    int norm() { return x * x + y * y; }
    int dot(P p) { return x * p.x + y * p.y; }
    int cross(P p) { return x * p.y - y * p.x; }

    P operator -() { return P(-x, -y); }
    P operator +(P p) { return P(x + p.x, y + p.y); }
    P operator -(P p) { return P(x - p.x, y - p.y); }
    P operator *(int k) { return P(x * k, y * k); }
};

const int MAX_N = 100;

int ccw(P p0, P p1, P p2) {
    P a = p1 - p0;
    P b = p2 - p0;

    if (a.cross(b) > 0) return 1;
    if (a.cross(b) < 0) return -1;
    if (a.dot(b) < 0) return 2;
    if (a.norm() < b.norm()) return -2;

    return 0;
}

int n;
P ps[MAX_N + 2];

int solve(int x, int y) {
    int c = 0;
    P p(x, y);

    for (int i = 0; i < n; i++) {
        P a = ps[i] - p;
        P b = ps[i + 1] - p;
        if (a.cross(b) == 0 && a.dot(b) <= 0) return 1;
        if (a.y > b.y) swap(a, b);
        if (a.y <= 0 && b.y > 0 && a.cross(b) > 0) c++;
    }

    return c % 2 ? 2 : 0;
}

int main() {
    cin >> n;
    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;

        ps[i] = P(x, y);
    }
    ps[n] = ps[0];
    ps[n + 1] = ps[1];

    int q;
    cin >> q;

    for (int i = 0; i < q; i++) {
        int x, y;
        cin >> x >> y;
        cout << solve(x, y) << endl;
    }
}

#include <bits/stdc++.h>
#define eps 1e-9
#define pi acos(-1)
#define nmax 200
#define f(c,a,b) for(int c=a; c<=b; c++)

using namespace std;
typedef double db;
struct P{
    db x, y;
    P(){}
    P(db x, db y) : x(x) , y(y) {}
    P operator - (P a){ return P(x-a.x, y-a.y); }
    db dot(P a){ return x*a.x+y*a.y; }
    db clen() { return sqrt(x*x+y*y); }
    db times(P a){ return x*a.y-y*a.x; }
}po[nmax];
typedef P V;
int n;

int sign(db x){ return x<-eps ? -1 : x>eps; }

int contain(P a){
    bool flag=false;
    f(i,1,n){
        P b = po[i]-a, c = po[i-1]-a;
        if( !sign(b.times(c)) && b.dot(c)<eps ) return 1;
        if(b.y < c.y) { P t=b; b=c; c=t; }
        if(c.times(b)>eps && b.y>eps && c.y<eps ) flag=!flag;
    }    
    return flag?2:0;
}

int main(){
    //freopen("owo.in","r",stdin);
    cin >> n;
    f(i,1,n) scanf("%lf%lf", &po[i].x, &po[i].y);
    po[0] = po[n];
    int q;
    cin >> q;
    while(q--) {
        P in;
        scanf("%lf%lf", &in.x, &in.y);
        printf("%d\n", contain(in));
    }
    return 0;
}

