int sgn(Real, Real);
namespace std {
  bool operator<(const Point &a, const Point &b){
    return sgn(real(a), real(b)) ? sgn(real(a), real(b)) < 0 : sgn(imag(a), imag(b)) < 0;
  }
  bool operator==(const Point &a, const Point &b){
    return sgn(real(a), real(b)) == 0 && sgn(imag(a), imag(b)) == 0;
  }
}
