/* }}} */
/* dot, cross {{{ */
Real dot(const Point &a, const Point &b){
  return real(conj(a) * b);
}

Real cross(const Point &a, const Point &b){
  return imag(conj(a) * b);
}
/* }}} */
/* ccw {{{ */
int ccw(const Point &a, const Point &b, const Point &c){
  Point u = b - a, v = c - a;
  if (sgn(cross(u, v)) > 0) return 1;
  if (sgn(cross(u, v)) < 0) return -1;
  if (sgn(dot(u, v)) < 0) return 2;
  if (sgn(norm(u), norm(v)) < 0) return -2;
  return 0;
}
/* }}} */
/* orthogonal, parallel {{{ */
bool orthogonal(const Line &l, const Line &m){
  return sgn(dot(l.q - l.p, m.q - m.p)) == 0;
}

bool parallel(const Line &l, const Line &m){
  return sgn(cross(l.q - l.p, m.q - m.p)) == 0;
}
/* }}} */
/* intersect {{{ */
bool intersectLL(const Line &l, const Line &m){
  return !parallel(l, m) ? true : parallel(l, Line(l.p, m.p));
}

bool intersectLS(const Line &l, const Segment &s){
  return sgn(cross(l.q - l.p, s.p - l.p)) * sgn(cross(l.p - l.p, s.q - l.p)) <= 0;
}

bool intersectLP(const Line &l, const Point &p){
  return ~ccw(l.p, l.q, p) & 1;
}

bool intersectSS(const Segment &s, const Segment &t){
  return ccw(s.p, s.q, t.p) * ccw(s.p, s.q, t.q) <= 0
      && ccw(t.p, t.q, s.p) * ccw(t.p, t.q, s.q) <= 0;
}

bool intersectSP(const Segment &s, const Point &p){
  return !ccw(s.p, s.q, p);
}
/* }}} */
/* project, refrect {{{ */
Point project(const Line &l, const Point &p){
  Real t = dot(p - l.p, l.p - l.q) / norm(l.p - l.q);
  return l.p + t * (l.p - l.q);
}

Point reflect(const Line &l, const Point &p){
  return p + 2.0l * (project(l, p) - p);
}
/* }}} */
/* distance {{{ */
Real distanceLP(const Line &l, const Point &p){
  return abs(p - project(l, p));
}

Real distanceLL(const Line &l, const Line &m){
  return intersectLL(l, m) ? 0.0l : distanceLP(l, m.p);
}

Real distanceLS(const Line &l, const Segment &s){
  if (intersectLS(l, s)) return 0.0l;
  return min(distanceLP(l, s.p), distanceLP(l, s.q));
}

Real distanceSP(const Segment &s, const Point &p){
  if (sgn(dot(s.q - s.p, p - s.p)) <= 0) return abs(s.p - p);
  if (sgn(dot(s.p - s.q, p - s.q)) <= 0) return abs(s.q - p);
  return distanceLP(s, p);
}

Real distanceSS(const Segment &s, const Segment &t){
  if (intersectSS(s, t)) return 0.0l;
  return min({
    distanceSP(s, t.p), distanceSP(s, t.q),
    distanceSP(t, s.p), distanceSP(t, s.q)
  });
}
/* }}} */
/* cross point {{{ */
Point crossPoint(const Line &l, const Line &m){
  Real a = cross(l.q - l.p, m.q - m.p);
  Real b = cross(l.q - l.p, l.q - m.p);
  if (sgn(a) == 0 && sgn(b) == 0) return m.p;
  if (sgn(a) == 0) assert(false);
  return m.p + b / a * (m.q - m.p);
}
/* }}} */
/* area {{{ */
Real area(const Polygon &g){
  Real s = 0.0l;
  for (int i = 0; i < (int)g.size(); i++){
    s += cross(g[i], g[(i + 1) % g.size()]);
  }
  return abs(s / 2.0l);
}
/* }}} */
int contains(const Polygon &g, const Point &p){
  int in = 0;
  rep(i, g.size()){
    Point a = g[i] - p, b = g[(i + 1) % g.size()] - p;
    if (imag(a) > imag(b)) swap(a, b);
    if (sgn(imag(a)) <= 0 && sgn(imag(b)) > 0){
      if (sgn(cross(a, b)) < 0) in ^= 1;
    }
    if (sgn(cross(a, b)) == 0 && sgn(dot(a, b)) <= 0) return 2;
  }
  return in;
}
/* }}} */

