		polyPoints.push_back(make_pair(x, y));
	}

	vector<seg> polygon;
	for (int i = 0; i < numPolyPoints; i++) {
		if (i == numPolyPoints-1) {
			polygon.push_back(make_pair(polyPoints[i], polyPoints[0]));
			break;
		}

		polygon.push_back(make_pair(polyPoints[i], polyPoints[i+1]));
	}


	int numQueries;
	cin >> numQueries;

	for (int q = 0; q < numQueries; q++) {
		double x, y;
		cin >> x >> y;
		pt p = make_pair(x, y);

		bool colin = false;
		for (auto side : polygon) {
			if ((p == side.first) || (zero(cross(p - side.first, side.second - side.first)) &&
				gradDir(make_pair(side.first, p)) == gradDir(make_pair(p, side.second)))) {
				// printPt(side.first);
				// cout << ", ";
				// printPt(p);
				// cout << ", ";
				// printPt(side.second);
				// cout << endl;

				// cout << grad(make_pair(side.first, p)) << " // " << grad(make_pair(p, side.second)) << endl;

				colin = true;
