#include <bits/stdc++.h>
using namespace std;

#define db long double
const db eps = 1e-6;
int sgn(db a, db b = 0) {
	a -= b;
	return (a > eps) - (a < -eps);
}

struct poi {
	db x, y;
	void r() { cin >> x; cin >> y; }
	void w() { cout << x << ' ' << y << '\n'; }
	poi operator -(poi p) {
		return {x - p.x, y - p.y};
	}
	poi operator +(poi p) {
		return {x + p.x, y + p.y};
	}
	poi operator *(db d) {
		return {x * d, y * d};
	}
	poi operator /(db d) {
		return {x / d, y / d};
	}
	bool operator ==(poi p) {
		return !sgn(x, p.x) && !sgn(y, p.y);
	}
	db dot(poi p) {
		return x * p.x + y * p.y;
	}
	db cross(poi p) {
		return x * p.y - y * p.x;
	}
	db len() { return sqrt(x * x + y * y); }
	db len2() { return x * x + y * y; }
	poi proj(poi p, poi q) {
		db s = (*this - p).dot(q - p) / (q - p).len2();
		return p + (q - p) * s;
	}
	db dis(poi p, poi q) {
		if((*this - p).dot(q - p) < 0) return (*this - p).len();
		if((*this - q).dot(p - q) < 0) return (*this - q).len();
		return abs((*this - p).cross(q - p) / (q - p).len());
	}
};
db xmul(poi a, poi b, poi c) {
	return (b - a).cross(c - a);
}

int ccw(poi a, poi b, poi c) {
	poi u = b - a, v = c - a;
	if(u.cross(v) > eps) return 1;
	if(u.cross(v) < -eps) return -1;
	if(u.dot(v) < -eps) return -2;
	if(u.len2() + eps < v.len2()) return 2;
	return 0;
}
bool si(poi a, poi b, poi c, poi d) {
	return ccw(a, b, c) * ccw(a, b, d) <= 0 &&
		ccw(c, d, a) * ccw(c, d, b) <= 0;
}
poi li(poi a, poi b, poi c, poi d) {
	db u = xmul(c, d, a), v = xmul(c, d, b);
	return (a * v - b * u) / (v - u);
}

const int N = 111;
poi p[N];

int main() {
	ios :: sync_with_stdio(false);
	int n; cin >> n;
	for(int i = 0; i < n; i ++)
		p[i].r();
	p[n] = p[0];
	int T; cin >> T;
	while(T --) {
		poi t; t.r(); poi s = {1e9L + 7, 1e9L + 9};
		bool on = false;
		int inter = 0;
		for(int i = 0; i < n; i ++) {
			if(!ccw(p[i], p[i + 1], t))
				on = true;
			if(si(s, t, p[i], p[i + 1]))
				inter ++;
		}
		if(on) cout << "1\n";
		else if(inter & 1) cout << "2\n";
		else cout << "0\n";
	}

	return 0;
}

