    return l.a + a * t;
}
P reflection(L l, P p) { // ??´???l???????????????p???????°?
    return p + vec(p, projection(l, p)) * 2;
}
int ccw(P a, P b, P c) {
    b = vec(a, b); c = vec(a, c);

    // a - b - c ????????????????????¨???
    if(sign(outp(b, c)) > 0) return +1; // ???????¨???????
    if(sign(outp(b, c)) < 0) return -1; // ????¨???????

    // a - b - c ?????´??????????????¶??¨???
    if(sign(inp(b, c)) < 0) return +2; // c - a - b
    if(norm(b) < norm(c))   return -2; // a - b - c
    return 0;                          // a - c - b
}
bool iSS(L s, L t) { // ????????????
    return ccw(s.a, s.b, t.a) * ccw(s.a, s.b, t.b) <= 0 &&
           ccw(t.a, t.b, s.a) * ccw(t.a, t.b, s.b) <= 0;
}
bool iSP(L s, P p) {
    return ccw(s.a, s.b, p) == 0;
}
D dSP(L s, P p) {
    P r = projection(s, p);
    if(iSP(s, r)) return abs(vec(p, r));
    return min(abs(vec(p, s.a)), abs(vec(p, s.b)));
}
D dSS(L s, L t) {
    if(iSS(s, t)) return 0;
    return min(min(dSP(s, t.a), dSP(s, t.b)),
               min(dSP(t, s.a), dSP(t, s.b)));
}
