bool isPointOnSegment(int px, int py, int sx, int sy, int ex, int ey){
	int ip = (px*ex+py*ey) - ((ex+px)*sx+(ey+py)*sy) + (sx*sx+sy*sy);
	return (py - ey)*(sx - ex) - (px - ex)*(sy - ey) == 0 && ip >=0 && ip <= (ex-sx)*(ex-sx)+(ey-sy)*(ey-sy);
}

int sign(int n){
	if(n>0)return 1;
	if(n<0)return -1;
	return 0;
}

float angle(int ax, int ay, int bx, int by, int cx, int cy){
	//angle between ab and ac
	float ab = atan2(by-ay,bx-ax);
	float ac = atan2(cy-ay,cx-ax);
	if(ab < 0)ab += 2*M_PI;
	if(ac < 0)ac += 2*M_PI;
	float ang = ac - ab;
	return (fabs(ang) < M_PI ? ang : -sign(ang) * 2 * M_PI + ang) * 180 / M_PI;
}

