		cin >> x >> y;
		res = 0;

		//is the point on segments
		for(int k=0;k<n;++k){
			if(isPointOnSegment(x,y,vtx[k],vty[k],vtx[(k+1)%n],vty[(k+1)%n])){
				res = 1;
				break;
			}
		}

		sum = 0;
		if(!res){
			//is the point in the polygon
			for(int k=0;k<n;++k){
				sum += angle(x,y,vtx[k],vty[k],vtx[(k+1)%n],vty[(k+1)%n]);
