        {
            bool x=0;
            REP(i,n) if(Line(g[i],g[i+1]).onsegment(p))
                x=1;
            if(x){PL(1);continue;}
        }
        Line X(p,p+Point(20001,1));
        bool out=1;
        REP(i,n)
        {
            Line Y(g[i],g[i+1]);
            Point o=X.intersect(Y).S;
            out^=X.onsegment(o)&&Y.onsegment(o);

        }
        PL(out?0:2);
    }

    return 0;
}

