bool onSeg(Point a,Point b,Point c)
{
    return eq(abs(a-b),abs(a-c)+abs(b-c));
}

double getArg(Point a,Point b,Point c)
{
    b -= a; c -= a;
    a = c*conj(b);
    return arg(a);
}

int contain(const vector<Point> &points,int sz,Point p)
{
    if(sz<3) return 0;
    for(int i=0;i<sz;i++)
    {
        if(onSeg(points[i],points[(i+1)%sz],p)) return 1;
    }
    double angle = 0;
    for(int i=0;i<sz;i++)
    {
        angle += getArg(p,points[i],points[(i+1)%sz]);
    }
    if(eq(angle,0)) return 0;
    return 2;
}

