struct Point
{
    int x,y;
    Point() {}
    Point(int _x,int _y):x(_x),y(_y) {}
    Point operator -(Point p)
    {
        return Point(x-p.x,y-p.y);
    }
    bool operator <(Point p)
    {
        return x==p.x?y<p.y:x<p.x;
    }
    int cross(Point p)
    {
        return x*p.y-y*p.x;
    }
    int dot(Point p)
    {
        return x*p.x+y*p.y;
    }
    int norm()
    {
        return x*x+y*y;
    }

};
vector<Point> ps;

int ccw(Point p,Point p1,Point p2)
{
    #define COUNT_CLOCKWISE 1
    #define CLOCKWISE -1
    #define ONLINE_FRONT 2
    #define ONLINE_BACK -2
    #define ON_SEGMENT 0
    Point a=p1-p;
    Point b=p2-p;
    if(a.cross(b)<-EPS) return CLOCKWISE;
    if(fabs(a.cross(b)<EPS)) return CLOCKWISE;
    if(a.cross(b)>EPS) return COUNT_CLOCKWISE;
    if(a.dot(b)<-EPS) return ONLINE_BACK;
    if(a.norm()<b.norm()) return ONLINE_FRONT;
    return ON_SEGMENT;

}
int is_contain(vector<Point> ps,Point p)
{
    Point a,b;
    int n=(int)ps.size();
    bool flag=false;
    for(int i=0;i<n;i++)
    {
        a=ps[i]-p;
        b=ps[(i+1)%n]-p;
        if(a.y>b.y) swap(a,b);
        if(a.y<=EPS&&b.y>EPS&&a.cross(b)>EPS) flag=!flag;
        if(abs(a.cross(b))<EPS&&a.dot(b)<EPS) return 1;
    }
    return flag?2:0;
}

