	REP(i, g){
		P q= polygon[i];
		P r = polygon[i+1];
		if(onCheck(p,q,r)==1){
			return 1; 	//p?????????qr??????????????´???
		}
		if(intersectionCheck(p, INFpoint, q,r)==1){
			S++;
		}
	}
	P q= polygon[g-1];
	P r = polygon[0];
	if(onCheck(p,q,r)==1){
		return 1; 	//p?????????qr??????????????´???
	}
