  vector<Vector> pt;
  Polygon(){}
  Polygon( int n ) : n(n), pt(n) {}
  int solve( Vector p );
};

int Polygon::solve( Vector p )
{
  Vector inf = Vector( 20000, p.y );
  Vector pd = Vector( p.x, p.y + sv );
  Vector infd = Vector( 20000, p.y + sv );

  int cnt = 0;

  for ( auto i = 0; i < n; ++i )
    {
      if ( Vector::boundaryCheck( pt[i], pt[(i+1) % n], p ) )
        {
          cout << "1" << endl;
          return 0;
        }
    }

  for ( auto i = 0; i < n; ++i )
    {
      int rcc = Vector::crossCheck( p, inf, pt[i], pt[(i+1) % n] );
      if ( rcc == 1 )
        ++cnt;
      else if ( rcc == 2 )
        {
          int rccd = Vector::crossCheck( pd, infd, pt[i], pt[(i+1) % n] );

          if ( rccd == 1 )
            ++cnt;
        }
    }

  if ( cnt % 2 == 0 )
    cout << "0" << endl;
  else
    cout << "2" << endl;

  return 0;
}

int main()
{
  int n;
  cin >> n;

  Polygon g( n );

  for ( auto i = 0; i < n; ++i )
    {
      double x, y;
      cin >> x >> y;
      g.pt[i] = Vector( x, y );
    }

  int q;
  cin >> q;

  for ( auto i = 0; i < q; ++i )
    {
      double x, y;
      cin >> x >> y;
      g.solve( Vector(x, y) );
    }

  return 0;
}

