double dot2(Point p0, Point p1) {
  return p0.x * p1.x + p0.y * p1.y; 
}

/*
double cross(Point p0, Point p1, Point p2) {
  return ( p1.x - p0.x ) * ( p2.y - p0.y ) - ( p2.x - p0.x) * (p1.y - p0.y);
}
*/

double cross2(Point p0, Point p1) {
  return p0.x * p1.y - p0.y * p1.x;
}

int isContained(vector<Point> polygon, Point p) {
  int n = polygon.size();
  bool flag = false;
  for(int i = 0; i < n; i++) {
    Point pre = polygon[i] - p; 
    Point next = polygon[ ( i + 1 ) % n ] - p;
    if(abs(cross2(pre, next)) < EPS && dot2(pre, next) < EPS)
      return 1;
    if(pre.y > next.y) 
      swap(pre, next);
    if(pre.y < EPS && next.y > EPS && cross2(pre, next) > EPS) 
      flag = !flag;
  }
  if(flag) 
    return 2;
  return 0;
}

