#include <vector>
#include <iostream>
void out(int);
class Point{
public:
	Point(const int &n, const int &m):x(n), y(m){};
	Point(){}
	int x, y;
};
class Line{
public:
	Line(const int &x1, const int &y1, const int &x2, const int &y2){
		if (y1 > y2){
			p1 = Point(x1, y1);
			p2 = Point(x2, y2);
		}else{
			p1 = Point{x2, y2};
			p2 = Point{x1, y1};
		}
	};
	Line() = default;
	Point p1, p2;
	int is_cross(const Point &p) const {
		if (p2.y != p1.y and p2.y != p.y and (p1.y - p.y) * (p2.y - p.y) <= 0){//辺がx軸に平行でなく、yが辺と交わる
			if (((p.y - p2.y) * (p1.x - p2.x)) > (p.x - p2.x) * (p1.y - p2.y)){//辺を通る
				return 1;
			}else if (((p.y - p2.y) * (p1.x - p2.x) == (p.x - p2.x) * (p1.y - p2.y))){//辺に乗る
				return 1000;
			}else{
				return 0;
			}
		}else if (p2.y == p1.y and p1.y == p.y){//x軸と平行な辺と触れる
			if ((p1.x - p.x) * (p2.x - p.x) <= 0){//辺と交わる
				return 1000;
			}else{
				return 0;
			}
		}else{
			return 0;
		}
	}
};
int fold(const std::vector<Line> &vertex, const Point p){
	int sum = 0;
	for (auto l : vertex){
		sum += l.is_cross(p);
	}
	if (sum >= 1000){
		return 1;
	}else if (sum % 2 == 1){
		return 2;
	}else{
		return 0;
	}
}
int main(){
	int n;
	std::cin >> n;
	std::vector<Line> lines;
	int sx, sy;
	std::cin >> sx >> sy;
	int px = sx, py = sy;
	for (auto i = 1; i < n; ++i){
		int x, y;
		std::cin >> x >> y;
		lines.push_back(Line(px, py, x, y));
		px = x; py = y;
	}
	lines.push_back(Line(px, py, sx, sy));
	int q;
	std::cin >> q;
	for (auto i = 0; i < q; ++i){
		int x, y;
		std::cin >> x >> y;
		std::cout << fold(lines, Point(x, y)) << "\n";
	}
}

