namespace std {
inline bool operator<(const Point &a, const Point &b) {
    if (sgn(a.real() - b.real()))
        return sgn(a.real() - b.real()) < 0;
    return sgn(a.imag() - b.imag()) < 0;
}
} // namespace std

double dot(const Point &a, const Point &b) { return real(conj(a) * b); }

double det(const Point &a, const Point &b) { return imag(conj(a) * b); }

struct Line {
    Point p1, p2;

    Line(Point p1 = Point(), Point p2 = Point()) : p1(p1), p2(p2) {}

    bool operator<(const Line &rhs) const {
        if (eq(p2, rhs.p2))
            return p1 < rhs.p1;
        return p2 < rhs.p2;
    }
    bool operator==(const Line &rhs) const {
        return (eq(p1, rhs.p1) && eq(p2, rhs.p2)) ||
               (eq(p1, rhs.p2) && eq(p2, rhs.p1));
    }
};

inline Point curr(const Polygon &p, const int &i){
    return p[i % p.size()];
}

inline Point next(const Polygon &p, const int &i){
    return p[(i+1) % p.size()];
}

inline Point prev(const Polygon &p, const int &i){
    return p[(i+p.size()-1) % p.size()];
}

enum { OUT, ON, IN };

int contains(const Polygon &poly, const Point &p){
    bool in = false;
    for(int i=0;i<poly.size();i++){
        Point a = curr(poly, i) - p, b = next(poly, i) - p;
        if(sgn(a.imag() - b.imag()) == 1)swap(a, b);
        if(sgn(a.imag()) <= 0 && sgn(b.imag()) > 0)
            if(sgn(det(a, b)) > 0)in = !in;
        if(sgn(det(a, b)) == 0 && sgn(dot(a, b)) <= 0)return ON;
    }
    return in ? IN : OUT;
}

