vector<L> comonTang(C c1, C c2) {
	vector<L> ret;
	if(c1.r > c2.r) swap(c1, c2);
	P sub = c1.p;
	c1.p -= sub;
	c2.p -= sub;
	double theta = atan2(c2.p.Y, c2.p.X);

	c2.p = spinP(c2.p, -theta);
	int signX = 0;
	if(c2.p.X > 0) signX = 1;
	if(c2.p.X < 0) signX = -1;

	double x = abs(c2.p.X), r1 = c1.r, r2 = c2.r;
	if(x*x > (r2 - r1) * (r2 - r1)) {
		double theta2 = asin((r2-r1)/x);
		ret.pb({spinP({0, r1}, theta2), spinP({sqrt(x*x-(r2-r1)*(r2-r1)), r1}, theta2)});
		ret.pb({spinP({0, -r1}, -theta2), spinP({sqrt(x*x-(r2-r1)*(r2-r1)), -r1}, -theta2)});
	}
	if(x > r1+r2) {
		double theta3 = PI / 2 - acos((r1+r2)/x);
		double len = sqrt((r1*x)*(r1*x)/(r1+r2)/(r1+r2)-r1*r1) + sqrt((r2*x)*(r2*x)/(r1+r2)/(r1+r2)-r2*r2);
		ret.pb({spinP({0, r1}, -theta3), spinP({len, r1}, -theta3)});
		ret.pb({spinP({0, -r1}, -theta3), spinP({len, -r1}, theta3)});
	}

	for(auto& l : ret) {
		if(signX == -1) l[1] = {-l[1].X, l[1].Y};
		l[0] = spinP(l[0], theta);
		l[1] = spinP(l[1], theta);
		l[0] += sub;
		l[1] += sub;
	}
	return ret;
}

