		for (int j = 0; j < n; j++){
			Point next = p[(j + 1) % n];
			if (ccw(p[j], next, t) == 0) state = 1;
			else if (ccw(p[j], next, t) < 0) m++;
			else pl++;
		}
		if (state) puts("1");
		else if (m == n || pl == n) puts("2");
		else puts("0");
	}

	return (0);
}

