#include <bits/stdc++.h>
using namespace std;

int x[300];
int y[300];
int n, q;

bool pointOnLine(int i, int j, int xx, int yy) {
    if (!((min(x[i], x[j]) <= xx && xx <= max(x[i], x[j])) &&
          (min(y[i], y[j]) <= yy && yy <= max(y[i], y[j]))))
        return false;
    return (x[j] - x[i]) * (yy - y[i]) == (y[j] - y[i]) * (xx - x[i]);
}

bool onLine(int xx, int yy) {
    for (int i = 0; i < n; i++)
        if (pointOnLine(i, (i + 1) % n, xx, yy))
            return true;
    return false;
}

bool intersects(int i, int j, int xx, int yy) {
    if (!(min(x[i], x[j]) <= xx && xx < max(x[i], x[j])))
        return false;
    int yint = ((float) (xx - x[i]) * (y[j] - y[i])) / ((float)(x[j] - x[i]));
    return yint > yy;
}

bool inPoly(int xx, int yy) {
    int c = 0;
    for (int i = 0; i < n; i++)
        c ^= intersects(i, (i+1) % n, xx, yy);
    return c;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d %d", x+i, y+i);
    }
    scanf("%d", &q);
    for (int i = 0; i < q; i++) {
        int xx, yy;
        scanf("%d %d", &xx, &yy);
        if (onLine(xx, yy))
            printf("1\n");
        else if (inPoly(xx, yy))
            printf("2\n");
        else
            printf("0\n");
    }
}

