		return(x == p.x && y == p.y);
	};
};

pt points[100];

inline pt operator-(pt a, pt b) {
	return pt(a.x-b.x, a.y-b.y);
}

inline double cross(pt a, pt b) {
	return(a.x*b.y - a.y*b.x);
}

inline double ccw(pt a, pt b, pt c) {
	return cross(b-a, c-a);
	// if A to B via c is left: > 0
	// if A to B via c is straight: < 1
	// if A to B via c is right: < 0
}


void find(pt p, int numPoints) {
	double r;
	pt a, b;
	for (int i = 0; i < numPoints; ++i) {
		if (p == points[i]) {
			printf("1\n");
			return;
		}
		r = ccw(points[i], p, points[(i+1)%numPoints]);

		if (r==0) {

			if (p.x >= std::min(points[i].x, points[(i+1)%numPoints].x) &&
				p.y >= std::min(points[i].y, points[(i+1)%numPoints].y) &&
				p.x <= std::max(points[i].x, points[(i+1)%numPoints].x) &&
				p.y <= std::max(points[i].y, points[(i+1)%numPoints].y)) {
				printf("1\n");
				return;
			}
			printf("0\n");

			return;
		} else if (r > 0) {
			printf("0\n");
			return;
		} 
	}
	printf("2\n");
}

