template<typename T>
T getDistanceFromPointToLine(const Point<T>& p, const Segment<T>& s) {
	const Point<T> a = p - s.p1;
	const Point<T> b = s.p2 - s.p1;
	const T outer = cross(a, b);
	return abs( outer / absolute(b) );
}

template<typename T>
Point<T> getIntersectionPointOfLineSegments(const Segment<T>& s1, const Segment<T>& s2)
{
	const T d1 = getDistanceFromPointToLine(s1.p1, s2);
	const T d2 = getDistanceFromPointToLine(s1.p2, s2);
	return s1.p1 + (s1.p2 - s1.p1) * (d1 / (d1 + d2));
}


