template<typename T>
Point<T> projectPointToSegment(const Point<T>& p, const Segment<T>& s)
{
	const Point<T> a = p - s.p1;
	const Point<T> b = s.p2 - s.p1;
	return s.p1 + (s.p2 - s.p1)*(dot(a, b) /norm(b));
}

template<typename T>
T getRadianFromCosineLaw(const T a, const T b, const T c)//get radian oppose to a
{
	return acos( 0.5*(b*b + c*c - a*a) / (b*c) );
}

