template<typename T>
Point<T> rotateVector(const Point<T>& p, const T rad)
{
	const T c = cos(rad);
	const T s = sin(rad);

	Point<T> ret;
	ret.x = p.x*c - p.y*s;
	ret.y = p.x*s + p.y*c;
	return ret;
}

template<typename T>
array<Point<T>, 2> getIntersectionOfCircles(const Circle<T>& c1, const Circle<T>& c2)
{

	const Point<T> v = c2.center - c1.center;
	const T d = absolute(v);
	const T rad = getRadianFromCosineLaw(c2.radius, c1.radius, d);
	const Point<T> unitV = unit(v);

	array<Point<T>, 2> ret;
	ret.at(0) = c1.center + rotateVector(unitV, rad)*c1.radius;
	ret.at(1) = c1.center + rotateVector(unitV, -rad)*c1.radius;
	return ret;
}

template<typename T>
int contains(const vector<Point<T>>& polygon, const Point<T>&p)
{
#if 1
	bool isIn = false;
	for (int end = polygon.size(), prev = end - 1, next = 0 ; next != end ; prev = next++) {
		Point<T> a = polygon.at(prev) - p;
		Point<T> b = polygon.at(next) - p;
		if (a.y > b.y) swap(a, b);
		const T outer = cross(a,b);
		if (outer < -EPS) continue;
		if (abs(outer) < EPS && dot(a, b) < EPS) return 1;

		//a.y < -EPS でなく、a.y < EPS としているのがポイント
		//境界の取り方が非常に巧妙。構成点が半直線上に存在しても、折れ線が半直線をを横切るときのみ、交差と判定されるのは2回でなく1回になる
		if (a.y < EPS && EPS < b.y)isIn = !isIn;
	}
	return isIn ? 2 : 0;
#else
	bool isIn = false;
	for (int end = polygon.size(), prev = end - 1, next = 0; next != end; prev = next++) {
		Point<T> a = polygon.at(prev) - p;
		Point<T> b = polygon.at(next) - p;
		if (abs(cross(a, b) < EPS && dot(a, b) < EPS))return 1;
		if (a.y > b.y) swap(a, b);
		if (a.y < EPS && EPS < b.y && cross(a, b) > EPS)isIn = !isIn;
	}
	return isIn ? 2 : 0;
#endif
}

