                        if (query_point.x < polygon_points[i].x) ++intersections;
                        else if (query_point.x == polygon_points[i].x) result = 1;
                    }
                }
            }
            else if (polygon_points[i].y > polygon_points[j].y) {
                if (polygon_points[i].y > query_point.y && query_point.y >= polygon_points[j].y) {
                    if (polygon_points[i].x < polygon_points[j].x) { // \ down
                        if (query_point.x < polygon_points[i].x) ++intersections;
