    if(fabs(a.Re - b.Re) < eps) return a.Im < b.Im;
    else return a.Re < b.Re;
}

double cross(const Point& a, const Point& b)
{
    return (conj(a) * b).Im;
}

double dot(const Point& a, const Point& b)
{
    return (conj(a) * b).Re;
}

int check(const Point& t)
{
    int res = 2;
    for(int i = 0; i < n; i++)
    {
        if(cross(P[i] - t, P[(i + 1) % n] - t) == 0 && dot(P[i] - t, P[(i + 1) % n] - t) < 0) res = 1;
        if(cross(P[i] - t, P[(i + 1) % n] - t) < 0) res = 0;
    }
    return res;
}

