using namespace std;

const double EPS = 1e-8;

int dcmp(double d) {
    if (fabs(d) < EPS) {
        return 0;
    }
    else {
        return d < 0? -1 : 1;
    }
}

struct Vector {
    double x, y;
    Vector(double x = 0, double  y = 0):x(x), y(y) {
    }
};

typedef Vector Point;

Vector operator-(const Vector& a, const Vector& b) {
    return Vector(a.x - b.x, a.y - b.y);
}

double cross(const Vector& a, const Vector& b) {
    return a.x * b.y - a.y * b.x;
}

double dot(const Vector& a, const Vector& b) {
    return a.x * b.x + a.y * b.y;
}

double ccw(const Point& a, const Point& b, const Point& c) {
    return cross(b - a, c - a);
}

int main() {
    int n; 
    cin >> n;
    vector<Point> g(n);
    for (int i = 0; i < n; i++) {
        cin >> g[i].x >> g[i].y;
    }

    int q;
    cin >> q;

    for (int i = 0; i < q; i++) {
        Vector p;
        cin >> p.x >> p.y;

        int count = 0; //0: on segment
        bool onseg = false;
        for (int i = 0; i < n; i++) {
            Vector u = g[i] - p;
            Vector v = g[(i + 1) % n] - p;
            //on segment
            if (dcmp(cross(u, v)) == 0 && dcmp(dot(u, v)) <= 0) {
                onseg = true;
                break;
            }
            if (u.y > v.y) {
                swap(u, v);
            }
            if (dcmp(cross(u, v)) > 0) {
                count++;
            }
        }
        if (onseg) {
            cout << 1 << endl;
        }
        else if (count % 2 == 0) {
            cout << 0 << endl;
        }
        else {
            cout << 2 << endl;
        }
    }

    return 0;
}

