int Contain(Polygon g, Point p) {
	int n = g.size();
	int cnt = 0;
	rop(i, 0, n) {
		Point a = g[i] - p, b = g[(i + 1) % n] - p;
		if(ccw(a, b, Point(0, 0)) == CCW_ON_SEGMENT) return 1;
		if(dcmp(a.y - b.y) == 0) continue;
		if(dcmp(a.y) == 0 && dcmp(a.x) >= 0) { 
			if(dcmp(b.y) == -1) ++ cnt;
			continue;
		}
		if(dcmp(b.y) == 0 && dcmp(b.x) >= 0) {
			if(dcmp(a.y) == -1) ++ cnt;
			continue;
		}
		if(a.y > b.y) swap(a, b);
		if(dcmp(a.y) <= 0 && dcmp(b.y) >= 0 && dcmp(a ^ b) == 1) ++ cnt;
	}
//	cerr << cnt << endl;
	return (cnt & 1) ? 2 : 0;
}

