struct Polygon : public vector<point>{
	//0->OUT, 1->ON, 2->IN
	int contains(const point &p) {
		bool in = false;
		int N = this->size();
		for (int i = 0; i < N; i++){
			point a = this->at(i) - p;
			point b = this->at((i + 1) % N) - p;
			if (imag(a) > imag(b)) {
				std::swap(a,b);
			}
			if (imag(a) <= 0 && 0 < imag(b))
				if (cross(a, b) < 0)in = !in;
			if (cross(a, b) == 0 && dot(a, b) <= 0)return 1;
		}
		return in ? 2 : 0;
	}
};

