using namespace std;

template<class T>
struct Vec2
{
	Vec2(){}
	Vec2(T _x, T _y) : x(_x), y(_y) {}

	Vec2 operator+(const Vec2& rhs) const { return Vec2(x + rhs.x, y + rhs.y); }
	Vec2 operator-(const Vec2& rhs) const { return Vec2(x - rhs.x, y - rhs.y); }
	Vec2 operator*(T s) const { return Vec2(x*s, y*s); }
	Vec2 operator/(T s) const { return Vec2(x/s, y/s); }
	T dot(const Vec2& rhs) const { return x*rhs.x + y*rhs.y; }
	T cross(const Vec2& rhs) const { return x*rhs.y - y*rhs.x; }
	T sqlength() const { return x*x + y*y; }
	double length() const { return sqrt(sqlength()); }
	bool operator<(const Vec2& rhs) const
	{
		if (x != rhs.x) return x < rhs.x;
		return y < rhs.y;
	}

	T x;
	T y;
};

template<class T>
T cross(const Vec2<T> &O, const Vec2<T> &A, const Vec2<T> &B)
{
	return (A - O).cross(B - O);
}

template<class T>
bool IsIntSegments(const Vec2<T>& A1, const Vec2<T>& A2, const Vec2<T>& B1, const Vec2<T>& B2)
{
	return (cross(A1, A2, B1)*cross(A1, A2, B2) <= 0 && cross(B1, B2, A1)*cross(B1, B2, A2) <= 0);
}

template<class T>
bool IsPointOnSegment(const Vec2<T>& P, const Vec2<T>& S, const Vec2<T>& E)
{
	Vec2<T> dir = E - S;
	if ((P - S).cross(dir) != 0) {
		return false;
	}
	T prd = (P - S).dot(dir);
	if (prd < 0) {
		return false;
	}
	return prd <= dir.dot(dir);
}

static unsigned int Rand()
{
	static unsigned int r = 123456789;
	r = r ^ (r << 13);
	r = r ^ (r >> 17);
	r = r ^ (r << 5);
	return r;
}

template<class T>
int CheckPolyInclude(const vector<Vec2<T> >& poly, Vec2<T> P)
{
	for (size_t i = 0; i < poly.size() - 1; i++) {
		if (IsPointOnSegment(P, poly[i], poly[i+1]) ) {
			return 1;
		}
	}

	Vec2<T> O;
	while (1) {
		bool ok = true;
		O = Vec2<T>(10000 + Rand() % 10000, Rand() % 10000);
		for (size_t i = 0; i < poly.size() - 1; i++) {
			if (IsPointOnSegment(poly[i], P, O)) {
				ok = false;
				break;
			}
		}
		if (ok) {
			size_t num = 0;
			for (size_t i = 0; i < poly.size() - 1; i++) {
				if (IsIntSegments(P, O, poly[i], poly[i + 1])) {
					num++;
				}
			}
			return num % 2 * 2;
		}
	}
}

